import React, { Component } from "react";

export default class Produit extends Component {
  state = {
    commentaire: "",
    list_db: [],
    showInfos: false,
  };

  cancelCourse = () => {
    this.setState({ commentaire: "" });
  };

  handleInfo = () => {
    this.setState({
      showInfos: !this.state.showInfos,
    });
  };

  onClickImage = (id) => {
    window.location.href = this.props.admin + "/showcomment/" + id;
  };

  render() {
    let { id, name, picture, info } = this.props.item;
    return (
      <div className="project" style={{ cursor: "pointer" }}>
        <button style={{ borderRadius: "25px" }} onClick={() => id}>
          <img src={picture} alt="" onClick={() => this.onClickImage(id)} />
          <h4>{name}</h4>
          <h5>{info}</h5>
        </button>
      </div>
    );
  }
}
